    static void testSerialization(element) {
        //do one specific example.
        Prepack prepack = new Prepack("Bastard","For absolute Bastards.","jadedResearcher",["reaction"], [Generator.fromDataString("reaction:___ N4Ig1gpgniBcICcIEMDGAXAlgewHYgBoR0oAHCOEAZQBUAlASQDkBxQkU7AZy8wCMANhABqyAQFcIXOAG0QMgPIB1JgFE6AOlqNWAWVW6FdAJobcyALYQAugAIkadFwK2+49LYGZIt5K+Rc6MgIACa2AO7Y4gIh7Ioq6lr0zCz6hiZmlja2AFbigfbImFxSvri20BB8CNjhccpqmtopaUam5lZ2AGZFAly26Ni2qMEQLnwQI-kQtgAWEADk-X58AUGhINYAvkA")],[new Scene.fromDataString("@everyone:___ -kIPYohAyU4yHF6QiEWSEGz3SHlVCXa7JW73CVFKCNM1PBCEFB9UBQZJ2P0gADS2GwdiC7FWcAg31VUXKtEsCApAGswBUqjUQPDelAEBBsLQmHQ7IgCjIoINEJgY8hgABfFttjsBKDDCB0ARUftDwc7Wh1disqD9uMgBNJ5TfOxJxQTBuqITtOCNBCyHgj9sITvd3tzkCEKCIMBFNPJZS++7P0uOU+7vuvggGKz6kKSIDDioo63uOk7TrOKBDsWpaEBWKANk2C6DkAA")],[]);
        prepack.activation_scenes.add(new Scene.fromDataString("Carol activates:___ -hwrEAcFyZQoGjadtQAZm-BMkxTZBs2IbtHW3ZhZjgcxjEHUoDhsFgUjSU9ewvftr3LUckkoUJB0fRt5SUNtRA7WxjHBKBEDAVI6NQJsWyURVqwbb9f1I-9-U-W9YMTQhk1THNF07IA"));

        Map<String, dynamic> serialization = prepack.getSerialization();
        Prepack p = Prepack.fromDataString("Bastard:___ -aHViMMCxMgASToAUYsTAZHOAFkEHcAFJUBDyO4I0GYRARODCSqkERQaY5FCzBLEWi+bCzRDYdxSMAAN0QsmwAA8lqgCtMAOpGPZ4CI1WR4cFGDIIvYI6Z4ACaEQYzEaXkIiiYhB8dC8AAFsCyqPJXNgojSNlsdq4DphkvJWFZvlM--HFaViGULU9SVMhqBbRk9VwI1zXYG1UgdYUYyglMkLQrC8I1GAigwPYJR3DUiiU0QdziM2j4Wbg+KEo0iVAA");
        print("testing prepack, p is $p, p.serialization is ${p.getSerialization()}");
        final Prepack recoveredPrepack = new Prepack.fromSerialization(serialization);
        UnitTests.processTest("Prepack can be serialized to and from datastring",DataStringHelper.serializationToDataString("TestEffect",prepack.getSerialization()), DataStringHelper.serializationToDataString("TestEffect",recoveredPrepack.getSerialization()), element);


    }
}